mapper.select:
select , p.project_name as projectName, l.name as locationName, l.id as locationId, alarm.node, alarm.type, alarm.record_time, alarm.info, alarm.remark, alarm.alarm_status, alarm.alarm_level from device_box_info d inner join device_box_location b on d.id = b.device_box_id inner join location_info l on b.location_id = l.id inner join project_info p on d.project_id = p.id left join device_alarm_info_log alarm on d.alarm_log_id = alarm.id WHERE 1=1
AND d.device_box_num=#{deviceBoxMac}
and d.device_box_num like CONCAT('%', #{deviceBoxNum}, '%') and d.sec_box_gateway like CONCAT('%', #{secBoxGateway}, '%') and d.stand_no like CONCAT('%', #{standNo}, '%')
AND d.project_id=#{projectId}
AND d.fx is null
AND l.root LIKE #{root}
order by ${sidx} ${order}
order by l.name
limit #{offset}, #{limit}

select count(*) from device_box_info d inner join device_box_location b on d.id = b.device_box_id inner join location_info l on b.location_id = l.id where 1=1
AND d.device_box_num=#{deviceBoxMac}
AND d.project_id=#{projectId}
AND l.root LIKE concat(#{root},'%')

mapper.update:
update device_box_info
`fx` = null, `fy` = null
where id = #{id}

mapper.delete:
delete from device_box_info a where a.id in (select b.device_box_id from device_box_location b where a.id = b.device_box_id and b.location_id in
#{locationId}
)

mapper.select:
SELECT LOCATION_ID FROM DEVICE_BOX_LOCATION where 1=1 AND DEVICE_BOX_ID IN
#{deviceBoxId}
GROUP BY LOCATION_ID HAVING COUNT(*) = 1

select
from device_box_info d,device_box_location l where 1=1 and d.id = l.device_box_id
and project_id = #{projectId}

select
from device_box_info d where 1=1
and device_box_num = #{deviceMac}
